public class Altura {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
	   
		double joao = 1.34;
		
		double pedro = 1.45;
		
		
		
		
		int ano =0;

		while(joao<=pedro){
			
			joao = joao + 0.025;
			
			pedro = pedro+ 0.02;
			
			ano = ano + 1;
			
		
		
			
		}
		
		System.out.println("joão será maior que pedro em "+ano+" anos	");
		
	}

}
